import { StyleSheet } from "react-native";

export const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#fff',
    alignItems: 'center',
    justifyContent: 'center',
  },
  blockGris: {
    position: 'relative',
    display: 'flex',
    justifyContent: 'center',
    width: '428px',
    height: '430px',
    backgroundColor: 'rgba(0,0,0,0.12)',
    paddingTop: '90px',
    paddingRight: '25px',
    paddingBottom: '20px',
  },
  imgCroix: {
    position: 'absolute',
    top: '30px',
    right: '20px',
    width: '65px',
    height: '65px',
  },
  imgChaussure: {
    width: '400px',
    height: '280px',
  },
  blockBlanc: {
    width: '428px',
    backgroundColor: 'white',
    borderTopLeftRadius: '20px',
    borderTopRightRadius: '20px',
    padding: '20px',
    marginTop: '-25px',
    border: "1px solid red",
  },
  marque: {
    fontSize: '14px',
    fontWeight: '650',
    color: '#cccccc',
  },
  model: {
    fontSize: '20px',
    fontWeight: '650',
    color: '#222222',
  },
  blockBoutons: {
    display: 'flex',
    flexDirection: 'row',
    justifyContent: 'space-between',
    paddingTop: '30px',
    paddingBottom: '80px',
  },
  boutonGauche: {
    display: 'flex',
    justifyContent: 'space-between',
    padding: '10px',
    paddingLeft: '20px',
    width: '180px',
    fontSize: '20px',
    fontWeight: 'bold',
    border: '3px solid #cccccc',
    paddingTop: '15px',
    paddingBottom: '15px',
    borderRadius: '4px',
  },
  boutonDroit: {
    display: 'flex',
    justifyContent: 'center',
    padding: '10px',
    width: '180px',
    // fontWeight: '650',
    border: '3px solid #222222',
    backgroundColor: '#222222',
    color: 'white',
    paddingTop: '15px',
    paddingBottom: '15px',
    borderRadius: '4px',
  },
  imgPanier: {
    width: '32px',
    height: '25px',
    marginRight: '10px',
  },
  textBuy: {
    fontSize: '20px',
  },
  blockText: {
    display: 'flex',
    flexDirection: 'row',
    flexWrap: 'wrap',
  },
  text: {
    fontSize: '18px',
  },
  textGras: {
    fontSize: '18px',
    fontWeight: 'bold',
  },
});
